mapper.insert:

INSERT INTO 
	TRANSACTION(COMPANY_IDX
				, DATE
				, TRANSACTION_TYPE
				, SUBJECT)
VALUES
	(#{company_idx}
	, #{date}
	, #{transaction_type}
	, #{subject})


INSERT INTO 
	ITEM(TRANSACTION_IDX
		, INVENTORY_ITEM_IDX
		, CONTENT
		, AMOUNT
		, UNIT_PRICE
		, SUPPLY_PRICE
		, TAX_PRICE
		, TOTAL_PRICE)
VALUES 
	(#{transaction_idx}
	, #{inventory_item_idx}
	, #{content}
	, #{amount}
	, #{unit_price}
	, #{supply_price}
	, #{tax_price}
	, #{total_price})

mapper.update:

UPDATE 
	TRANSACTION
SET
	DATE = #{date}
	, TRANSACTION_TYPE =  #{transaction_type}
	, SUBJECT = #{subject}
WHERE
	TRANSACTION_IDX = #{transaction_idx}


UPDATE
	ITEM
SET
	INVENTORY_ITEM_IDX = #{inventory_item_idx}
	, CONTENT = #{content}
	, AMOUNT = #{amount}
	, UNIT_PRICE = #{unit_price}
	, SUPPLY_PRICE = #{sypply_price}
	, TAX_PRICE = #{tax_price}
	, TOTAL_PRICE = #{total_price}
WHERE
	ITEM_IDX = #{item_idx}


UPDATE
	MEMO
SET
	CONTENT = #{content}
WHERE
	MEMO_IDX = #{memo_idx}


UPDATE
	FILE
SET
	FILE_NAME = #{file_name}
	, FILE_PATH = #{file_path}
WHERE
	FILE_IDX = #{file_idx}


UPDATE
